insert into tblFree(seq, seqMember, title, content, regdate, thread, depth) values(seqFree.nextVal, 915, '예약 취소 시 패널티 과다~', '처음 에어비앤비를 호스트하는데요실수로 스마트 금액으로 설정해놨는데예약이 되었어요 그래서 금액을 변경하고 게스트에게 요금 변경을 요구했는데거절을 않하시고 저에게 예약취소를 하라고 하시는데 맞는 얘기인지 다른분은 변경된 금액요청에 거절을 하셔서 예약이 취소 되었는데이분은 저보고 하라고 하시는데 어느것이 맞는지 모르는 초보 호스트라 묻습니다부탁드립니다~ㅜㅜ(제가 하면 페널티던데...)몹시떠들어대다', '2019-04-29', 0, 934000);
insert into tblFree(seq, seqMember, title, content, regdate, thread, depth) values(seqFree.nextVal, 916, '달력이 비어 있는데 예약 수락이 안 되네요 (급한 상황)', '처음 가입한 호스트 입니다대금수령 방법을 페이팔로 했다 계좌송금으로 바꾸려고 합니다계좌는 한국의 은행계좌 모두 되는 건가요?', '2018-06-07', 0, 935000);
